mapper.select:
select

from sys_user
where id = #{id}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

limit 1

select

from sys_user
where real_name LIKE CONCAT('%',#{realName},'%')


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

select

from sys_user
where username = #{username}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_user
where user_email = #{userEmail}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_user
where nickname = #{nickname}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_user
where mobile_phone = #{mobilePhone}


	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_user
where id in

	#{item}



	and tenant_id = #{tenantId}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by create_date desc

select

from sys_user



		and id = #{id}


		and username = #{username}


		and nickname = #{nickname}


		and real_name = #{realName}


		and user_password = #{userPassword}


		and password_salt = #{passwordSalt}


		and user_email = #{userEmail}


		and telephone = #{telephone}


		and mobile_phone = #{mobilePhone}


		and avatar_url = #{avatarUrl}


		and gender_enum = #{genderEnum}


		and user_type_enum = #{userTypeEnum}


		and tenant_id = #{tenantId}


		and register_type_enum = #{registerTypeEnum}


		and register_origin_enum = #{registerOriginEnum}


		and state_enum = #{stateEnum}


		and create_user_id = #{createUserId}


		and delete_enum = #{deleteEnum}


		and create_date >= #{createDateStartDate}


		and create_date <= #{createDateEndDate}


		and update_date >= #{updateDateStartDate}


		and update_date <= #{updateDateEndDate}




		order by

			${item}



		order by create_date desc

select

from sys_user
where state_enum = #{stateEnum}


	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_user
where delete_enum = #{deleteEnum}


	and tenant_id = #{tenantId}

order by create_date desc

select

from sys_user
where delete_enum = #{deleteEnum} and state_enum = #{stateEnum}


	and tenant_id = #{tenantId}

order by create_date desc

mapper.update:
update sys_user
set
state_enum = #{stateEnum},
update_date = #{updateDate},
update_user_id = #{updateUserId}

where id in

	#{item}


	and tenant_id = #{tenantId}

update sys_user
set
delete_enum = #{deleteEnum},
delete_date = #{deleteDate},
delete_user_id = #{deleteUserId}

where id in

	#{item}


	and tenant_id = #{tenantId}
