mapper.select:
select

from sys_param_type
where id = #{id}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

limit 0,1

select

from sys_param_type
where type_code = #{typeCode}


	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

select

from sys_param_type
where type_code in

	#{item}



	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

select

from sys_param_type
where id in

	#{item}



	and state_enum = #{stateEnum}


	and delete_enum = #{deleteEnum}

order by ranking asc

select

from sys_param_type



		and id = #{id}


		and type_name = #{typeName}


		and type_code = #{typeCode}


		and ranking = #{ranking}


		and description = #{description}


		and state_enum = #{stateEnum}


		and create_user_id = #{createUserId}


		and delete_enum = #{deleteEnum}


		and create_date >= #{createDateStartDate}


		and create_date <= #{createDateEndDate}


		and update_date >= #{updateDateStartDate}


		and update_date <= #{updateDateEndDate}


order by ranking asc

mapper.update:
update sys_param_type set state_enum = #{stateEnum}, update_date = #{updateDate}, update_user_id = #{updateUserId}
where id in
#{item}

update sys_param_type set delete_enum = #{deleteEnum}, update_date = #{updateDate}, update_user_id = #{updateUserId}, delete_date = #{deleteDate}, delete_user_id = #{deleteUserId}
where id in
#{item}
